arguments.argument:
var i, color;

draw_set_halign(fa_center)
draw_set_valign(fa_middle)

for (i=0; i<3; i+=1) {
    if choice[i] = "" break

    //Hover color
    color = colorNormal
    draw_set_alpha(.8)
    if hover = i {
        draw_set_alpha(.9)
        color = colorHover
    }

    //Button
    draw_set_color(color)
    draw_roundrect(x-width, y-height+(spacing * i), x+width, y+height+(spacing * i), false)
    //draw_set_color(c_black)
    //draw_roundrect(x-width, y-height+(spacing * i), x+width, y+height+(spacing * i), true)

    //Text
    draw_set_color(c_black)
    draw_set_alpha(1)
    draw_text(x, y+(spacing * i), choice[i])
}
