arguments.argument:
//Textbox alpha
if alphaSpd = 0
    alpha = alphaTo
else
    alpha = approach(alpha, alphaTo, alphaSpd)

//Clear text when textbox alpha is 0
if alpha = 0 {
    name     = ""
    affi     = ""
    drawText = ""
}

//Text
if text = "" exit

if !init {
    init = true
    pos  = 0
    len  = string_length(text)
}

//Advance dialog
if mouse_check_button_pressed(mb_left) or keyboard_check_pressed(vk_enter) {
    if pos = len {
        init = false
        text = ""
        Parser.pending = false
    } else {
        pos = len - 1
    }
}

//Typewriter effect
if pos < len {
    pos     += textSpd
    drawText = string_copy(text, 0, pos)
}

//Advance icon
nextCycle += 0.1
if nextCycle > pi2
    nextCycle -= pi2
